/// 商店配置-craze需求表
/// </summary>
public class IapProductConfig : BaseSheet<IapProductConfig_Data>
{
    private List<IapProductConfig_Data> _config = null;

    public List<IapProductConfig_Data> GetIapConfig()
    {
        if (_config == null || _config.Count == 0)
        {
            bool aoe = Record.GetInt("AFBack", 0) == 1;
            _config = new List<IapProductConfig_Data>();
            for (int i = 0; i < dataList.Count; i++)
            {
                if (aoe)
                {
                    if (dataList[i].ItemID > 10000)
                    {
                        _config.Add(dataList[i]);
                    }
                    
                }
                else
                {
                    if (dataList[i].ItemID < 10000)
                    {
                        _config.Add(dataList[i]);
                    }
                }
            }
        }

        return _config;
    }
