using System;    public class WndObj    {        private String _className;        public String className        {            get            {                return _className;            }        }        private bool _needToCloseLastWindow;        public bool needToCloseLastWindow        {            get            {                return _needToCloseLastWindow;            }        }        private Object _data;        public Object data        {            get            {                return _data;            }        }        public WndObj(String className, bool needToCloseLastWindow = true, Object data = null)        {            _className = className;            _needToCloseLastWindow = needToCloseLastWindow;            _data = data;        }    }